'use strict';

var easesJsnext = require('eases-jsnext');

function draw(node, params) {
	const len = node.getTotalLength();

	return {
		delay: params.delay,
		duration: params.duration || 800,
		easing: params.easing || easesJsnext.cubicInOut,
		css: (t, u) => `stroke-dasharray: ${t * len} ${u * len}`
	};
}

module.exports = draw;
